            balls1[i] = Number(element.runs);
            wickets1[i] = Boolean(element.wickets);
        });
        this.ballsInnings2.forEach((element, i) => {
            balls2[i] = Number(element.runs);
            wickets2[i] = Boolean(element.wickets);
        });
