            // console.log(context.dataset.label);
            let index = context.dataIndex;
            let value = false;
            if (context.dataset.label == "Innings 1")
                value = wickets1[index];
            else
                value = wickets2[index];
            return value ? 5 : 0.5;
        }
    }
